import { Navbar, Nav, NavDropdown, Image } from 'react-bootstrap';
import './component.less';
import { useState } from 'react';

function NavItem() {
  let list = [
    { name: '首页', url: '/' },
    { name: '核心技术', url: '/middle' },
    { name: '智能驾驶', url: '/auto' },
    { name: '新闻中心', url: '/news' },
    { name: '关于我们', url: '/about' },
    { name: '加入我们', url: '/joinus' },
    { name: '开发者社区', url: '/development' },
  ];
  const [NavList, setNavList] = useState(list);
  const mounseEnter = (e, item, index) => {
    console.log(e, 'eeee');
    console.log(item, 'item');

    item['mounseIn'] = true;
    // setNavList([...item, [item]]);
    let newArray = NavList.map((isItem) => {
      isItem = item;
      return isItem;
    });

    setNavList(newArray);
  };
  const onMouseLeave = (e, item, index) => {
    console.log(item, '离开==');
    item['mounseIn'] = false;
    let arr = NavList;
    arr[index] = item;
    setNavList(NavList);

    setNavList(arr);
  };
  return (
    <Nav>
      {NavList.map((item, index) => {
        console.log('改变了哦', item);
        return (
          <Nav.Link
            key={index}
            className={item['mounseIn'] ? 'moutrue' : null}
            onMouseEnter={(event) => mounseEnter(event, item, index)}
            onMouseLeave={(event) => onMouseLeave(event, item, index)}
          >
            {item.name}
          </Nav.Link>
        );
      })}
    </Nav>
  );
}
export default function Header() {
  return (
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
      <Navbar.Brand href="#home">
        <Image className="logo" src={'/static/logo2x.png'} fluid />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="mr-auto">
          {/* <Nav.Link href="#features">Featu</Nav.Link>
          <Nav.Link href="#pricing">Pricing</Nav.Link> */}
        </Nav>
        <NavItem />
      </Navbar.Collapse>
    </Navbar>
  );
}
