import { Navbar, Nav, NavDropdown, Image } from 'react-bootstrap';
import './component.less';
import Router from 'next/router';
import { useState } from 'react';

//导航子项
function NavItem() {
  let list = [
    { name: '首页', url: '/' },
    { name: '核心技术', url: '/middle' },
    { name: '智能驾驶', url: '/auto' },
    { name: '新闻中心', url: '/news' },
    { name: '关于我们', url: '/about' },
    { name: '加入我们', url: '/joinus' },
    { name: '开发者社区', url: '/development' },
  ];
  const [NavList, setNavList] = useState(list);
  const mounseEnter = (e, item, index) => {
    item['mounseIn'] = true;
    let arr = [...NavList];
    arr[index] = item;

    setNavList(arr);
  };
  const onMouseLeave = (e, item, index) => {
    item['mounseIn'] = false;
    let arr = [...NavList];
    arr[index] = item;
    setNavList(arr);
  };
  return (
    <Nav id="parent-Nav">
      {NavList.map((item, index) => {
        return (
          <Nav.Link
            key={index}
            className={item['mounseIn'] ? 'moutrue blue-link' : null}
            onMouseEnter={(event) => mounseEnter(event, item, index)}
            onMouseLeave={(event) => onMouseLeave(event, item, index)}
          >
            {item.name}
          </Nav.Link>
        );
      })}
    </Nav>
  );
}
export default function Header() {
  console.log(Router, 'Router');
  const [isWhite, setIsWhite] = useState(false);
  const parentMouseIn = (e) => {
    setIsWhite(true);
    e.stopPropagation();
  };
  const parentMouseLeave = (e) => {
    setIsWhite(false);
    e.stopPropagation();
  };
  return (
    <Navbar
      onMouseEnter={(event) => parentMouseIn(event)}
      onMouseLeave={(event) => parentMouseLeave(event)}
      collapseOnSelect
      expand="lg"
      bg="dark"
      variant="dark"
      className={isWhite ? 'bg-white' : ''}
    >
      <Navbar.Brand href="#home">
        <Image
          className="logo"
          src={isWhite ? '/static/blue-logo.png' : '/static/logo2x.png'}
          fluid
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="mr-auto">
          {/* <Nav.Link href="#features">Featu</Nav.Link>
          <Nav.Link href="#pricing">Pricing</Nav.Link> */}
        </Nav>
        <NavItem />
      </Navbar.Collapse>
    </Navbar>
  );
}
